import { useState } from "react";

export default function UpdateCounter(){
    var [count, setCounter] = useState(0); //getter, setter
    
    var [empDetails, setEmpDetails] = useState({
        userName: "Mayank",
        userDesignation: "trainer",
        userAge: 10
    });

    function incrementCounter(){
        setCounter((oldCounterVal) =>{
            if(oldCounterVal==10){
                return 100;
            }
            return oldCounterVal + 1;
        });
        console.log(count);
    }

    function changeName(inputString){
        setEmpDetails((oldUserName)=>{
            return oldUserName = inputString;
        });
        console.log(empDetails.userName);
    }

    return (
        <div>
            <h1> The counter is {count}</h1>
            <input type="button" value="Increment" onClick={incrementCounter} />
            <h1> The name is {empDetails.userName} </h1>
            <input type="button" value="ChangeName" onClick={(event) => changeName("Kartik")} />
        </div>
    )
}
